const sectionCenter = document.querySelector('.section-center');    

const sectionCart = document.querySelector('.section-cart');

const cartItem = document.querySelector('.cart');

const imageCart = document.querySelector('.item-cart');
const cartNotification = document.querySelector('.section-cart-notification');

const btnCategory = document.querySelectorAll('.name-product');

let arrProductCart = [];

const numberCart = document.querySelector('.number-cart');
let products = [];

let btnDelete = [];

let totalCart = 0;

window.onload = () => {
    displayMenuItem(menu);
    products = document.querySelectorAll('.product-section');
    btnCategory.forEach( (item,index) => {
        item.addEventListener('click',(event) => {
            const category = event.currentTarget.dataset.id;
            const menuCategory = menu.filter((menuItem) => {
                if(menuItem.category === category){
                    return menuItem;
                }
            })
            if(category === 'all'){
                displayMenuItem(menu);
            }
            else
                displayMenuItem(menuCategory);
            products = document.querySelectorAll('.product-section');
            renderProductCart(products);
            
        })
    })
    renderProductCart(products);
}

const numberTotalCart = (length) => {
    numberCart.innerHTML = length + 1 ;
}

window.addEventListener('click', (e) => {
    if(e.target == imageCart){
        sectionCart.classList.toggle("show");
    }
    
}) 


cartItem.addEventListener('click', () => {
    renderProductInCart(arrProductCart);
    btnDelete = document.querySelectorAll('.btn-delete'); 
    btnDelete.forEach( (btnD,index) => {
        btnD.addEventListener( 'click', (e) => {
            e.currentTarget.parentElement.parentElement.remove();
            arrProductCart.splice(index,1);
            renderProductInCart(arrProductCart);
            numberTotalCart(arrProductCart.length-1);
        })
    })  
})

const renderProductCart = (products) => {    
    products.forEach((product,index) => {
        product.addEventListener('click', (e) =>{
            const idProduct = e.currentTarget.dataset.id;
            const index = Number(idProduct) - 1 ;
            let productItem = menu[index];
            let text = false;
            arrProductCart.forEach((item,index) => {
                if(item.stt === productItem.stt)
                    text = true;
            })
            if(text === false){
                numberTotalCart(arrProductCart.length);
                arrProductCart.push(productItem);
            }
            renderProductInCart(arrProductCart);
        })})
}


const displayMenuItem = (menuItem) => {
    let displayMenu = menuItem.map((item) => {
        return `<div data-id = ${item.stt} class="pt-3 col-md-3 product-section">
                    <div class="product">
                        <img class="img-product" src=${item.src} alt="">
                        <div class="picture-title">
                            <p>Price: ${item.price}$</p>
                            <p>Click Buy</p>
                            </div>
                    </div>
                </div>`;
    } )
    
    displayMenu = displayMenu.join("");
    sectionCenter.innerHTML= displayMenu;
}

const renderProductInCart = (productItem) => {
    let render = productItem.map((item) => {
        return      `<div class="product-cart">
                        <img src= ${item.src} alt="">
                        <p class="p-category">Category: ${item.category}</p>
                        <p class="p-price">Price: ${item.price} $</p>
                        <div class = "btn-cart">
                            <button data-id = ${item.stt} style="background-color: black; color: white;" class="btn btn-buy">Buy</button>
                            <button data-id = ${item.stt} style="background-color: black; color: white;" class="btn btn-delete">Delete</button>
                        </div>
                    </div>`;
    })
    render = render.join("");
    if(render !== "")
    sectionCart.innerHTML = render;
    if(render == "")
    sectionCart.innerHTML = `<h1 class = section-cart-notification>Don't have product</h1>`
}